resultMap.insert:
INSERT
  INTO RESERVATION A
(
A.RESERVE_NO
, A.RESERVE_DAY
, A.RESERVE_TIME
)
VALUES
(
  SEQ_RESERVE_NO.NEXTVAL
, #{ reserve_day }
, #{ reserve_time }
)

resultMap.update:
 UPDATE RESERVATION
  SET

, RESERVE_DAY =#{ reserve_day }
  , RESERVE_TIME=#{ reserve_time }
  WHERE RESERVE_NO=#{ reserve_no }

resultMap.delete:
 DELETE
   FROM RESERVATION
	 WHERE RESERVE_NO = #{ reserve_no }
