mapper.insert:
INSERT
         INTO RESERVATION A
       (
       A.RESERVE_NO
       , A.RESERVE_DAY
       , A.RESERVE_TIME
       , A.USER_NO
       , A.MY_NO
       )
       VALUES
       (
         SEQ_RESERVE_NO.NEXTVAL
       , #{ reserve_day }
       , #{ reserve_time }
       , #{ user_no }
       , #{ my_no }
       )

mapper.update:
 UPDATE RESERVATION
  SET

RESERVE_DAY =#{ reserve_day }
  , RESERVE_TIME=#{ reserve_time }
  WHERE RESERVE_NO=#{ reserve_no }

mapper.delete:
 DELETE
   FROM RESERVATION
	 WHERE RESERVE_NO = #{ reserve_no }

mapper.insert:
INSERT
  INTO PL_USER
(
  USER_NO
, USER_ID
, USER_PWD
, USER_NAME
, PHONE
)
VALUES
(
  SEQ_USER_NO.NEXTVAL
, #{user_id}
, #{user_pwd}
, #{user_name}
, #{phone}
)

mapper.select:
SELECT *
  FROM PL_USER

	WHERE USER_NAME = #{value}
    AND STATUS = 'N'		



	WHERE USER_ID = #{value}
	AND STATUS = 'N'

mapper.update:
UPDATE
       PL_USER


     , USER_PWD = #{user_pwd}


     , USER_NAME = #{user_name}


     , PHONE = #{phone}


 WHERE USER_ID = #{user_id}
 AND STATUS = 'N'
